INSERT INTO `tzht_bulletin` VALUES ('3', '北京家有儿女巅峰大脑教育科技有限公司入驻幸会平台！', '<p><span style=\"color: rgb(51, 51, 51); font-family: SimSun; font-size: 14px;\">&nbsp;</span><span style=\"margin: 0px; padding: 0px; font-family: SimSun; font-size: 14px; color: rgb(102, 102, 102);\">北京家有儿女巅峰大脑教育科技有限公司是一家集儿童娱乐、儿童潜能开发研究为一体的综合性潜能开发服务企业，旗下包括早教、全脑开发、婴幼儿游泳等多个项目，家有儿女以创造快乐与和谐的家庭为支点，以早期潜能开发为杠杆，以智能和体能开发为目标，满足了更多家长和孩子的需求。家有儿女的发展特点实现了品牌化、数字化、智能化和标准化。遵循“天道、师道、孝道”这一企业精髓，以“影响、感召、激励”的教育经营理念，赢得孩子和家长的赞同及认可，是孩子学习成长的快乐港湾和幸福家园。</span></p><p style=\"text-align: center;\"><img src=\"/Uploads/ueditor/image/20180605/1528192389.jpg\" title=\"1528192389.jpg\" alt=\"201776141745260.jpg\"/></p><p><img src=\"/Uploads/ueditor/image/20180605/1528192531.jpg\" title=\"1528192531.jpg\" alt=\"2016122211231126.jpg\"/></p><p><img src=\"/Uploads/ueditor/image/20180605/1528192422.jpg\" title=\"1528192422.jpg\" alt=\"201826152435799.jpg\"/></p>', '1524562434', '幸会官方', '50', '1');
INSERT INTO `tzht_bulletin` VALUES ('4', '欢迎王后雄教育科技有限公司入驻幸会平台', '<p><span style=\"margin: 0px; padding: 0px; line-height: 28px; font-size: 14px;\">王后雄教育科技有限公司是由王后雄教育品牌研究院、广东教育出版社、七色光国际教育集团共同投资成立的股份制企业，是一家集高端教辅及培训教材研发、视频解读在线资源制作、版权输出、图书出版发行等综合型教育科技文化创意公司。</span></p><p><span style=\"margin: 0px; padding: 0px; line-height: 28px; font-size: 14px;\">&nbsp; &nbsp; &nbsp;&nbsp;王后雄教育科技有限公司旗下拥有学考优文化发展有限公司、王后雄教育品牌研究院（王后雄教授为院长）、广东教育出版社教辅研究院等图书发行及研究机构，拥有3项注册商标和5项新概念教辅发明专利。公司与华中师范大学考试研究院、广东教育出版社、七色光国际教育集团、颂大教育科技股份有限公司（上市公司）、学习周报、西安博创软件有限公司（导学号）、湖北言道文化传播有限公司（状元桥）等10余家机构、公司结成了战略合作伙伴关系，共同开启中国教育发展的新纪元。</span></p><p><span style=\"margin: 0px; padding: 0px; line-height: 28px; font-size: 14px;\">&nbsp; &nbsp; &nbsp;&nbsp;王后雄教育历经20多年的品牌打造，目前拥有最具核心竞争力的研发团队，优秀作者团队达500多位名师。每年发行的1.2亿册畅销教辅风靡全国，近7千万中小学师生从王后雄教育中受益。《教材完全解读》、《重难点手册》、《考点同步解读》、《课堂完全解读》连续荣登2015、2016中国品牌教辅TOP榜“同步类”金奖，《高考完全解读》荣获2016中国品牌教辅TOP榜“高考类”银奖，创造了中国教辅出版的神话......</span></p><p><span style=\"margin: 0px; padding: 0px; line-height: 28px; font-size: 14px;\"><br/></span></p><p><br/></p>', '1528253660', '幸会官方', '50', '1');
